function startQuiz(type) {
    app.startQuiz(type);
}

function previousQuestion() {
    app.previousQuestion();
}

function nextQuestion() {
    app.nextQuestion();
}

function finishQuiz() {
    app.finishQuiz();
}

function reviewAnswers() {
