    if (event.key === 'Enter') {
        const nextBtn = document.getElementById('next-btn');
        const finishBtn = document.getElementById('finish-btn');

        if (finishBtn.style.display !== 'none' && !finishBtn.disabled) {
            app.finishQuiz();
        } else if (!nextBtn.disabled) {
            app.nextQuestion();
        }
    }
