// WebAuthn utility functions
function generateChallenge() {
  const array = new Uint8Array(32);
  crypto.getRandomValues(array);
  // base64url形式で返す
  const binary = Array.from(array, byte => String.fromCharCode(byte)).join('');
  return btoa(binary).replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}

function generateSessionToken() {
  const array = new Uint8Array(32);
  crypto.getRandomValues(array);
  return Array.from(array, byte => byte.toString(16).padStart(2, '0')).join('');
}

function base64ToArrayBuffer(base64) {
  // base64url -> base64
  let base64Fixed = base64.replace(/-/g, '+').replace(/_/g, '/');
  while (base64Fixed.length % 4) base64Fixed += '=';
  const binary = atob(base64Fixed);
  const buffer = new ArrayBuffer(binary.length);
  const view = new Uint8Array(buffer);
  for (let i = 0; i < binary.length; i++) {
    view[i] = binary.charCodeAt(i);
  }
  return buffer;
}

function arrayBufferToBase64(buffer) {
  const bytes = new Uint8Array(buffer);
  let binary = '';
  bytes.forEach((b) => binary += String.fromCharCode(b));
  return btoa(binary);
}

function arrayBufferToBase64url(buffer) {
  return arrayBufferToBase64(buffer).replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}

