    if (request.method !== 'GET') {
        return;
    }

    // リクエストはそのまま使用
    const actualRequest = request;

    // WebAuthn APIリクエストは常にネットワークから取得（キャッシュしない）
    if (request.url.includes('/api/auth/passkey/')) {
        event.respondWith(
            fetch(actualRequest).catch(() => getOfflineResponse(request))
        );
