async function handleStudySessionStart(request, env, corsHeaders) {
  try {
    const { userId, subject, level } = await request.json();

    if (!userId || !subject || !level) {
      return jsonResponse({
        success: false,
        error: 'userId, subject, level が必要です'
      }, 400, corsHeaders);
    }

