// 問題保存機能
async function saveQuestions(request, env, subject, corsHeaders) {
  try {
    const { questions } = await request.json();

    if (!questions || !Array.isArray(questions)) {
      return jsonResponse({ error: '無効な問題データです' }, 400, corsHeaders);
