  <script>
    // ========== Fantasyコンポーネント実装 ==========
    function initFantasy() {
      if (document.getElementsByClassName("fantasy").length !== 1) {
        return;
      }

      var cvs = document.getElementById("cvs");
      if (!cvs) return;

      var ctx = cvs.getContext("2d");
      var display = document.getElementById("display");
      var displayCtx = display.getContext("2d");
      var screenImage = document.getElementById("screenImage");
      var screenImageCtx = screenImage.getContext("2d");
      var rili = document.getElementById("rili");
      var riliCtx = rili.getContext("2d");

      var songInfo = {};
      var AllTimeBak = 0;
      var NowBak = 0;
      var DrawWarning = false;
      var EnMonth = false;

      // カレンダー描画関数（元の仕様通り）
      function drawRili() {
        riliCtx.clearRect(0, 0, 600, 600);
        var date = new Date();
        var year = date.getYear();
        var mouth = date.getMonth();
        var today = date.getDate();
        var week = date.getDay();

        var cardSize = 40;
        var array_three = [4, 6, 9, 11];
        var array_threeone = [1, 3, 5, 7, 8, 10, 12];
        var array_week = ["SUN", "MON", "TUES", "WED", "THUR", "FRI", "SAT"];

        var firstDraw;
        var wIdx = (today - 1) % 7;

        if (week >= wIdx) {
          firstDraw = week - wIdx;
        } else {
          firstDraw = week - wIdx + 7;
        }

        var dayIndex = 1;
        var countDay = 30;
        var row = 6;

        if (array_three.indexOf(mouth + 1) > -1) {
          countDay = 30;
        } else if (array_threeone.indexOf(mouth + 1) > -1) {
          countDay = 31;
        } else {
          if ((year % 4 == 0 && year % 100 != 0) || year % 400 == 0)
            countDay = 29;
          else countDay = 28;
        }

        function drawTodaybg(i, j) {
          riliCtx.save();
          riliCtx.beginPath();
          riliCtx.strokeStyle = "#900";
          riliCtx.arc(
            45 + j * cardSize * 1.7 + cardSize / 1.18,
            50 + i * cardSize + cardSize / 2,
            cardSize / 2 - 10,
            -Math.PI,
            Math.PI * 1
          );
          riliCtx.stroke();
          riliCtx.closePath();
          riliCtx.restore();
        }

        var isNum = /^\d+(\d+)?$/;

        function drawDate(txt, i, j) {
          riliCtx.textAlign = "center";
          riliCtx.fillStyle = "rgb(69,68,84)";
          riliCtx.font = cardSize / 1.5 + "px Impact";
          var yOffest = 3;

          if ((j == 0 || j == 6) && isNum.test(txt)) {
            riliCtx.fillStyle = "#900";
          }

          riliCtx.fillText(
            txt.toString(),
            45 + j * cardSize * 1.7 + cardSize / 1.18,
            50 + i * cardSize + (cardSize / 3) * 2 + yOffest
          );

          if (txt == today) {
            drawTodaybg(j, i);
          }
        }

        // 月表示
        riliCtx.fillStyle = "rgb(69,68,84)";
        riliCtx.font = "900 26pt SimHei";
        riliCtx.textAlign = "center";
        var monthCN = ["一","二","三","四","五","六","七","八","九","十","十一","十二"];
        var monthEN = [
          " January",
          "February",
          "  March",
          "  April",
          "   May",
          "  June",
          "  July",
          " August",
          "September",
          " October",
          "November",
          " December",
        ];

        if (EnMonth) {
          riliCtx.scale(1.1, 1);
          riliCtx.fillText(monthEN[mouth], 245, 32);
          riliCtx.resetTransform();
        } else {
          riliCtx.scale(1.1, 1);
          riliCtx.fillText(monthCN[mouth] + "月", 260, 32);
          riliCtx.resetTransform();

          riliCtx.font = "20pt SimHei";
          riliCtx.textAlign = "end";
          riliCtx.fillText(today + "日", 520, 38);
        }

        // グリッド線を描画
        for (var i = 0; i < row; i++) {
          for (var j = 0; j < 7; j++) {
            riliCtx.strokeRect(
              45 + j * cardSize * 1.7,
              50 + i * cardSize,
              cardSize * 1.7,
              cardSize
            );
          }
        }

        dayIndex = 1;

        // 日付を描画
        for (var i = 0; i < row; i++) {
          for (var j = 0; j < 7; j++) {
            if (i == 0) {
              // 最初の行に曜日を描画
              drawDate(array_week[j], i, j);
              continue;
            }

            if (i == 1 && j < firstDraw) {
              // 1日の描画位置を確定
              continue;
            }

            if (dayIndex > countDay) {
              // 月の日数のみを描画
              break;
            }

            drawDate(dayIndex, i, j);
            dayIndex++;
          }
        }
      }

      // 表示キャンバスにコピー
      function copyToDisplay() {
        display.width = rili.width;
        display.height = rili.height;
        displayCtx.drawImage(rili, 0, 0);
      }

      // 初期化
      drawRili();
      copyToDisplay();

      // 毎分更新
      setInterval(function() {
        drawRili();
        copyToDisplay();
      }, 60000);
    }

    // ========== GlobalTipコンポーネント実装 ==========
    function showGlobalTip() {
      var hours = new Date().getHours();
      var minutes = new Date().getMinutes().toString().padStart(2, '0');
      var seconds = new Date().getSeconds().toString().padStart(2, '0');

      var message = '';
      var type = 'info';
      var iconClass = 'icon-info';

      if (hours >= 6 && hours < 9) {
        message = `おはようございます~~，現在 ${hours}:${minutes}:${seconds}，朝ごはんを食べましたか？🍔`;
      } else if (hours >= 9 && hours < 12) {
        message = `おはようございます~~，現在 ${hours}:${minutes}:${seconds}，サボらないように気をつけてください🐟~~`;
      } else if (hours >= 12 && hours < 13) {
        message = `こんにちは~~，現在 ${hours}:${minutes}:${seconds}，お昼のご飯の時間です🍚~~`;
        type = 'success';
        iconClass = 'icon-dagouyouquan';
      } else if (hours >= 13 && hours < 14) {
        message = `こんにちは~~，現在 ${hours}:${minutes}:${seconds}，忙しい午後も適度に休憩してください😴~~`;
      } else if (hours >= 14 && hours < 17) {
        message = `こんにちは~~，現在 ${hours}:${minutes}:${seconds}，午後も頑張ってください🐟~~`;
      } else if (hours >= 17 && hours < 19) {
        message = `夕方になりました~~，現在 ${hours}:${minutes}:${seconds}，夕食の準備をしましょう🥗🍖~~`;
        type = 'warning';
        iconClass = 'icon-gantanhao';
      } else if (hours >= 19 && hours < 20) {
        message = `こんばんは~~，現在 ${hours}:${minutes}:${seconds}，一日お疲れ様でした，運動もしましょう🏃‍♂️~~`;
        type = 'success';
        iconClass = 'icon-dagouyouquan';
      } else if (hours >= 20 && hours < 23) {
        message = `こんばんは~~，現在 ${hours}:${minutes}:${seconds}，夜もこっそり勉強しましょう💯~~`;
      } else if (hours >= 23 && hours < 24) {
        message = `こんばんは~~，現在 ${hours}:${minutes}:${seconds}，そろそろお風呂に入って寝ましょう🛀🥱😪~~`;
        type = 'warning';
        iconClass = 'icon-gantanhao';
      } else {
        message = `夜更かししないで~~，現在 ${hours}:${minutes}:${seconds}，早く寝て，朝日を一緒に楽しみましょう~~😇🛏`;
        type = 'warning';
        iconClass = 'icon-gantanhao';
      }

      var tipElement = document.getElementById('globalTip');
      var contentElement = tipElement.querySelector('.tip-content');
      var timeElement = tipElement.querySelector('.tip-time');
      var iconElement = tipElement.querySelector('.icon');

      contentElement.textContent = message;
      timeElement.textContent = `${hours}:${minutes}:${seconds}`;

