 */
async function handleMarkAsMastered(request, env, corsHeaders) {
  try {
    const { wrongAnswerId } = await request.json();

    if (!wrongAnswerId) {
      return jsonResponse({
        success: false,
        error: 'wrongAnswerId が必要です'
      }, 400, corsHeaders);
